import{_ as h}from"./TableOfContents.bdd6f6dd.js";import{_ as b}from"./client-only.ac4e2e02.js";import v from"./Icon.b194f7c3.js";import{_ as f}from"./ModuleStructure.26e66338.js";import{_ as g}from"./nuxt-link.6699b7d4.js";import{_}from"./CardInfo.eb661db5.js";import{u as y}from"./composables.961be93c.js";import{_ as A,o as w,c as q,b as o,w as t,a as e,d as a,g as C,h as i,p as T,e as P}from"./entry.98172cbe.js";const s=n=>(T("data-v-ef14abe7"),n=n(),P(),n),O={class:"flex flex-col items-start lg:flex-row-reverse lg:justify-end xl:gap-10 2xl:gap-20"},j={class:"e-article print:not-prose",lang:"el",style:{hyphens:"auto"}},z=i('<h1 class="mb-0" data-v-ef14abe7>Ναρκωτικά</h1><h5 class="font-normal uppercase leading-tight" data-v-ef14abe7>Οδηγός Εκπαιδευτικού</h5><section id="s-1" data-v-ef14abe7><h2 id="s-1t" data-v-ef14abe7>Περιγραφή ενότητας</h2><section class="e-card e-module__meta" data-v-ef14abe7><table class="m-0 leading-tight" data-v-ef14abe7><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Μαθητικός πληθυσμός που απευθύνεται το υλικό:</b></td><td data-v-ef14abe7>Το παρόν μαθησιακό υλικό απευθύνεται σε μαθητές ηλικίας 15-18 ετών που φοιτούν είτε στο ενιαίο Λύκειο είτε σε Τεχνική Σχολή.</td></tr><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Αναμενόμενο Χρονικό Διάστημα Εφαρμογής:</b></td><td data-v-ef14abe7>4-6 διδακτικές περιόδους των 45 λεπτών</td></tr></table></section><p data-v-ef14abe7> Στην παρούσα ενότητα οι μαθητές καλούνται να καταρρίψουν ή να επιβεβαιώσουν μύθους σχετικά με τις ναρκωτικές ουσίες. </p><p data-v-ef14abe7> Μέσω ενός εισαγωγικού βίντεο, παρουσιάζεται η ιστορία ενός αγοριού και στη συνέχεια οι μαθητές καλούνται να αναστοχαστούν και να εκφράσουν τις σκέψεις τους σχετικά με αυτό. Πολλοί είναι οι μύθοι που επικρατούν στην κοινωνία σχετικά με τις ναρκωτικές ουσίες. </p><p data-v-ef14abe7> Αφότου οι μαθητές μελετήσουν τους πιο διαδεδομένους μύθους για τις ναρκωτικές ουσίες, καλούνται να επιλέξουν ένα από αυτούς ώστε να τον διερευνήσουν. </p><p data-v-ef14abe7> Βάσει της φύσης του θέματος, η συγκεκριμένη ενότητα είναι σχεδιασμένη ώστε να εφαρμόζεται σε δευτερεύοντες πηγές πληροφόρησης (π.χ. πηγές πληροφόρησης από το διαδίκτυο). Διαμέσου της ενότητας οι μαθητές μαθαίνουν να αξιολογούν την αξιοπιστία των πηγών τους είτε αυτές προέρχονται από επιστημονικές πηγές, είτε από τα μέσα μαζικής ενημέρωσης, και να αναπτύξουν περαιτέρω δεξιότητες διερεύνησης. </p><p data-v-ef14abe7> Έπειτα, αφότου διατυπώσουν τα συμπεράσματά τους, οι μαθητές ετοιμάζουν μία παρουσίαση που περιλαμβάνει τα αποτελέσματά τους και απευθύνεται στους συμμαθητές τους ή άλλο κοινό. Η παρουσίαση θα πρέπει να είναι σχετική και να πείθει το κοινό (π.χ. μέσω του Power Point ή εντός βίντεο). Η ενότητα ολοκληρώνεται με τη μελέτη μίας περίπτωσης κατά τη διάρκεια της οποίας οι μαθητές καλούνται να επιλύσουν ένα ηθικό δίλημμα αξιοποιώντας τις γνώσεις που έχουν αποκτήσει από την ενότητα και ενσωματώνοντας τις προσωπικές τους, τις κοινωνικές τους και ηθικές τους αξίες. </p><p data-v-ef14abe7> Είναι στην κρίση του κάθε εκπαιδευτικού αν θα επιλέξει να εφαρμόσει και τα δύο θέματα, την κάνναβη και τη χρήση MMS, κατά τη διδασκαλία της ενότητας ή όχι. Μπορεί δηλαδή να επιλέξει να ακολουθήσει μόνο ένα από τα δύο θέματα εάν το επιθυμεί. </p></section><hr class="mt-10 mb-40 print:my-10" data-v-ef14abe7><section id="s-2" data-v-ef14abe7><h2 id="s-2t" data-v-ef14abe7>Στόχοι της ενότητας:</h2><ul class="leading-tight" data-v-ef14abe7><li data-v-ef14abe7><b data-v-ef14abe7>Citizenship competence:</b> develop students&#39; knowledge and attitudes towards making responsible decisions about cannabis use and/or the “miracle drugs”. </li><li data-v-ef14abe7><b data-v-ef14abe7>Media competence:</b><ul class="my-0" data-v-ef14abe7><li data-v-ef14abe7>ddevelop students&#39; skills in assessing the reliability of the information, presenting the evidence in a manner relevant to a given audience,</li><li data-v-ef14abe7>reacting adequately and responsibly to the misinformation presented in (social)media.</li></ul></li><li data-v-ef14abe7><b data-v-ef14abe7>Digital competence:</b> develop students&#39; skills in using digital media while presenting their inquiry results to the other groups. </li><li data-v-ef14abe7><b data-v-ef14abe7>Science competence:</b><ul class="my-0" data-v-ef14abe7><li data-v-ef14abe7>develop students&#39; understanding of science as a way to know, as a social practice, and why we should trust science;</li><li data-v-ef14abe7>develop students&#39; attitudes of honesty, objectivity, intellectual humility, and open-mindedness;</li><li data-v-ef14abe7>develop their ability to plan and devise procedures for testing hypotheses and interpreting the findings;</li><li data-v-ef14abe7>develop further students&#39; knowledge about cannabis: mechanism of action, differences between chemical and biological aspects of different types of cannabis; OR/AND</li><li data-v-ef14abe7>develop an understanding of MMS&#39;s chemical and biological properties and the severe health risks of its use as a drug.</li></ul></li><li data-v-ef14abe7><b data-v-ef14abe7>Social competence:</b> develop students&#39;... <ul data-v-ef14abe7><li data-v-ef14abe7> improved understanding of the personal and social risks associated with cannabis use, incl of its legislative aspects; </li><li data-v-ef14abe7> enhanced understanding of the importance of personal choices and values related to cannabis in managing personal well-being. </li></ul></li></ul></section><hr class="mt-10 mb-40 print:my-10" data-v-ef14abe7><section id="s-3" data-v-ef14abe7><h2 id="s-3t" data-v-ef14abe7>Expected prior knowledge</h2><ul class="leading-tight" data-v-ef14abe7><li data-v-ef14abe7>Prior understanding of the structure of organic compounds.</li><li data-v-ef14abe7>Properties of waves</li><li data-v-ef14abe7>Light as a form of energy from the sun</li><li data-v-ef14abe7>Natural and artificial radiation sources</li><li data-v-ef14abe7>Structure of an atom</li><li data-v-ef14abe7>Prior knowledge of psychological and social impacts of narcotics use.</li></ul></section><hr class="mt-10 mb-40 print:my-10" data-v-ef14abe7>',8),D={id:"s-4"},S=s(()=>e("h2",{id:"s-4t"},"Module structure",-1)),M=s(()=>e("p",null,"This module consists of 6 activities. The sequence of activities is given in Figure 1 and Table 1 (see below).",-1)),I=i('<p class="mt-20" data-v-ef14abe7><b data-v-ef14abe7>Table 1.</b> Learning sequence of the “Drugs” module </p><table data-v-ef14abe7><thead data-v-ef14abe7><tr data-v-ef14abe7><th class="w-1/5" data-v-ef14abe7><b data-v-ef14abe7>Activity No</b></th><th data-v-ef14abe7>Description</th></tr></thead><tbody data-v-ef14abe7><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Activity 1</b></td><td data-v-ef14abe7>Presentation of an introductory video</td></tr><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Activity 2</b></td><td data-v-ef14abe7> Discussion in groups<br data-v-ef14abe7> Choosing a myth to be busted </td></tr><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Activity 3</b></td><td data-v-ef14abe7> In groups: <ul data-v-ef14abe7><li data-v-ef14abe7>Posing a testable hypothesis / research question;</li><li data-v-ef14abe7>Searching for relevant information (Activity 3) to confirm or falsify the posed hypothesis/research question;</li><li data-v-ef14abe7>Assessing the reliability of the found evidence;</li><li data-v-ef14abe7>Making conclusions.</li></ul></td></tr><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Activity 4</b></td><td data-v-ef14abe7>Transforming evidence to a format relevant to a given audience (peers): using a tutorial. Students learn how to produce a short video or use other visualisation methods instead, after which they plan and execute their ideas.</td></tr><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Activity 5</b></td><td data-v-ef14abe7>Group presentations (coupled with peer assessment activities).</td></tr><tr data-v-ef14abe7><td data-v-ef14abe7><b data-v-ef14abe7>Activity 6</b></td><td data-v-ef14abe7>Students, individually and in groups, consider a moral dilemma related to cannabis (6a) or MMS (6b).</td></tr></tbody></table>',2),E=s(()=>e("hr",{class:"mt-10 mb-40 print:my-10"},null,-1)),N=s(()=>e("section",{id:"s-5"},[e("h2",{id:"s-5t"}," Requirements for physical environment "),e("p",null," Smartphone, computer, internet, display equipment for demonstrating videos. A3 or A2 paper, markers of different colours. ")],-1)),$=s(()=>e("hr",{class:"mt-10 mb-40 print:my-10"},null,-1)),L={id:"s-6"},U=s(()=>e("h2",{id:"s-6t"},"Assessment",-1)),W=s(()=>e("p",null,"Students can be assessed differently throughout the module, including science process skills, general competencies, such as argumentation skills, and topic-related content knowledge. Types of assessments will include formative assessments based on observation, individual/group worksheets and summative assessments based on group presentations.",-1)),R=s(()=>e("p",null,[e("b",null,"formative:")],-1)),V=s(()=>e("li",null,"Oral / written feedback from the teacher (based on observations, questions asked, etc.) on individual / group work throughout the module.",-1)),B=s(()=>e("p",null,[e("b",null,"Summative:")],-1)),F=s(()=>e("ul",null,[e("li",null,"Grades assigned by the teacher on the group presentation (based on the students’ presentation and the student’s ability to provide relevant answers / comments)."),e("li",null,"Grades assigned by the teacher on group or individual worksheets.")],-1)),Q=s(()=>e("hr",{class:"mt-10 mb-40 print:my-10"},null,-1)),J={id:"s-7"},G=s(()=>e("h2",{id:"s-7t"},"Teaching suggestions",-1)),K=i('<p data-v-ef14abe7> This module could also be part of a school drug and risk behaviour prevention programme. The teacher must be familiar with that programme and/or collaborate with other professionals (e.g., school counsellors, youth workers, etc.) in case personal situations related to consumption arises during the module and students might need support. The teacher should remember that students&#39; confidentiality should be respected except when there is evidence or suspicion that someone is at risk. </p><p data-v-ef14abe7> The module starts with an <a href="https://www.youtube.com/watch?v=cN5KV_WJubw" target="_blank" rel="noopener noreferrer" data-v-ef14abe7>introductory video</a> for opening up the topic &quot;Drugs&quot; to raise further questions in students rather than giving them immediate answers. It is expected that after watching the video (<b data-v-ef14abe7>Activity 1</b>), students in groups will feel intrinsically motivated to examine drug-related myths and beliefs more thoroughly. The intention is to involve students in committing to activities that relate to a better understanding of the issue – an issue seen by students as relevant to their lives, not simply to the curriculum. It also draws students&#39; attention to thinking about their prior knowledge and sharing their conceptions and views with peers (<b data-v-ef14abe7>Activity 2</b>). In Activity 2, students are introduced to 8 myths about cannabis and three myths about a &quot;miracle&quot; drug such as MMS, from which they must choose one to be further busted. Activity 2 should help students narrow down their chosen myth into a hypothesis/research question that can be falsified / confirmed / answered (<b data-v-ef14abe7>Activity 3</b>). It may be that students need help when trying to pose it. Therefore, the teacher could help them by drawing examples of good (e.g. testable) and bad hypotheses / questions before they pose them themselves. The <a href="https://www.thoughtco.com/testable-hypothesis-explanation-and-examples-609100#:~:text=Updated%20on%20January%2012%2C%202019,experiment%20using%20the%20scientific%20method." target="_blank" rel="noopener noreferrer" data-v-ef14abe7>following resource</a> could be helpful for that. </p><p data-v-ef14abe7> The <a href="https://www.youtube.com/playlist?list=PLuC4DJElPQrOmTPUWRrZh_W5VtuNTDsdI" target="_blank" rel="noopener noreferrer" data-v-ef14abe7>&quot;fact videos&quot;</a> can also be demonstrated in this step to recall or study drug-related facts. Also, selected chapters from Scientific background information can be used for this purpose. </p><p data-v-ef14abe7> The drugs-related myths cannot be busted (debunked) experimentally in the classroom. Therefore, students are expected to gather evidence from secondary sources to confirm or falsify their hypothesis / answer to their research question (<b data-v-ef14abe7>Activity 3</b>). By juxtaposing media/data sources and their content, students critically analyse their reliability and make justified conclusions based on the evidence. As reliability assessment can be a real challenge for students, the student is supplied with a <span class="bg-rose-100" data-v-ef14abe7>tool</span> for searching and analysing the information. Also, the teacher could explain its use by demonstrating and analysing reliable and less reliable sources as learning examples before the students implement this independently. </p><p data-v-ef14abe7> To learn how to communicate their findings, students are guided to produce a video or other types of visualisation to present their conclusions (<b data-v-ef14abe7>Activity 4</b>). For this purpose, students can use different tutorials. Also, student material is provided with criteria the presentation should fulfil. These criteria can be used formatively for self-assessment by students during the process and for peer assessment when presenting the video and answering the questions of their peers and the teacher (<b data-v-ef14abe7>Activity 5</b>). For the last, students can use the following assessment tool to give feedback to the other groups. </p><p data-v-ef14abe7> In the last activity (<b data-v-ef14abe7>Activity 6</b>), the storyline of the module returns to the introductory video and the presented myths. Based on the lessons learned from the earlier stages, students prepare to make decisions on an individual or group level in the format of a case study where they have an opportunity to juxtapose and reflect on their learned science knowledge but also on their personal and social values. </p>',6),Z=i('<p data-v-ef14abe7> The intro video and follow-up activities are intended to inspire reflection on decision-making related to cannabis use and the various consequences that may result from particular decisions. They encourage the viewer to consider different options during the decision-making process and how cannabis-related choices might affect or impact existing relationships. There is support in educational literature acknowledging how young people are more likely to learn when encouraged to think things through in a supportive manner. </p><p data-v-ef14abe7> A <b data-v-ef14abe7>moral dilemma</b> presented at the end of the module (<b data-v-ef14abe7>Activity 6</b>) should make students think about their and other people&#39;s choices and the consequences of these choices to different stakeholders involved in a given situation. The potential decisions and reasoning behind them are mainly based on the Kohlbergian framework of moral reasoning, which includes the following (examples to illustrate the levels are taken from activity 6, NB! other interpretations deviating from the given are also possible): </p><h3 data-v-ef14abe7> Level 1 (Pre-Conventional) </h3><ol data-v-ef14abe7><li data-v-ef14abe7><b data-v-ef14abe7>Obedience and punishment orientation.</b> Individuals focus on the direct consequences of their actions on themselves. An action is perceived as morally wrong because the perpetrator is punished. Common in children. Rules are fixed and absolute. The main question is, &quot;how can I avoid punishment? <br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should not keep it because this could result in a criminal record for him/her, which means (s)he is a bad person.</i></li><li data-v-ef14abe7><b data-v-ef14abe7>Self-interest</b> (instrumental-relativist) orientation: Paying for a benefit. Right behaviour is defined by whatever the individual believes to be in their best interest, or whatever is &quot;convenient,&quot; but understood in a narrow way which does not consider one&#39;s reputation or relationships to groups of people. Reasoning shows a limited interest in the needs of others, but only to a point where it might further the individual&#39;s own interests. <br data-v-ef14abe7><i data-v-ef14abe7>E.g., Cris should keep it because Marcus has done many kind things to Cris in the past. Now (s)he can &quot;pay&quot; it back.</i><br data-v-ef14abe7><i data-v-ef14abe7>Cris should return it to Marcus because such a criminal friend will be useless.</i></li></ol><h3 data-v-ef14abe7> Level 2 (Conventional) </h3><ol start="3" data-v-ef14abe7><li data-v-ef14abe7><b data-v-ef14abe7>Interpersonal accord and conformity with social norms.</b> Individuals are receptive to approval or disapproval from others as it reflects society&#39;s views. They try to be a &quot;good boy&quot; or &quot;good girl&quot; to live up to these expectations, having learned that being regarded as good benefits the self. Stage three reasoning may judge the morality of an action by evaluating its consequences in terms of a person&#39;s relationships, which now begin to include things like respect, gratitude, and the &quot;golden rule&quot;. Ability to recognise good or bad intentions. The good boy/girl attitude. <br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should keep it because (s)he cannot disappoint his long-term friend.</i><br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should give it back not to disappoint his/her parents in case (s)he gets caught with drugs by police.</i></li><li data-v-ef14abe7><b data-v-ef14abe7>Authority and social-order maintaining orientation:</b> Law and order morality, ability to see abstract normative systems (&quot;Do your duty&quot;). It is important to obey laws, <a href="https://en.wikipedia.org/wiki/Dictum" target="_blank" rel="noopener noreferrer" data-v-ef14abe7>dicta</a>, and <a href="https://en.wikipedia.org/wiki/Social_convention" target="_blank" rel="noopener noreferrer" data-v-ef14abe7>social conventions</a> because of their importance in maintaining a functioning society. Moral reasoning in stage four is thus beyond the need for individual approval exhibited in stage three. A central ideal or ideals often prescribe what is right and wrong. If one person violates a law, perhaps everyone would—thus, there is an obligation and a duty to uphold laws and rules. When someone does violate a law, it is morally wrong. <br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should give it back to Marcus because owning and consuming weed is forbidden and, therefore, illegal.</i></li></ol><h3 data-v-ef14abe7> Level 3 (Post-Conventional) </h3><ol start="5" data-v-ef14abe7><li data-v-ef14abe7><b data-v-ef14abe7>Social contract orientation</b> - different opinions, rights, and values should be mutually respected as unique to each person or community. Laws are regarded as <a href="https://en.wikipedia.org/wiki/Social_contract" target="_blank" rel="noopener noreferrer" data-v-ef14abe7>social contracts</a> rather than rigid edicts. Personal rights are important. This is achieved through majority decision and inevitable compromise. Democratic government is supposedly based on stage five reasoning. <br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should keep it to avoid his/her friend from getting a criminal penalty, which may lead him even deeper into his personal downfall.</i><br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should give it back, as trying to keep Marcus from being convicted for a small amount of narcotics may pull him into a more significant narcotics-related affair. He could get a life lesson to avoid dealing drugs.</i><br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should not keep it as this could result in a criminal record for him/her and prevent him/her from entering the military academy.</i><br data-v-ef14abe7><i data-v-ef14abe7>E. g. Cris should give it back as acting otherwise would not be fair towards him/her being punished for the act that (s)he has not committed.</i></li><li data-v-ef14abe7><b data-v-ef14abe7>Universal ethical principles</b> - moral reasoning is based on <a href="https://en.wikipedia.org/wiki/Abstraction" target="_blank" rel="noopener noreferrer" data-v-ef14abe7>abstract reasoning</a> using universal ethical principles. The resulting consensus is the action taken. The individual acts because it is right and not because it avoids punishment, is in their best interest, is expected, is legal, or previously agreed upon. <br data-v-ef14abe7><i data-v-ef14abe7>E.g. Cris should give it back and let Marcus take all the responsibility for what he has done. Narcotics have ruined many people&#39;s lives. It is just not right to support their use and propagation.</i></li></ol><hr data-v-ef14abe7><p data-v-ef14abe7> Through activity 6, students are expected to develop a broader understanding of the issue while considering different perspectives and consequences of one or another decision. Also, it is hoped that as a result of phase 4 (answering the following questions): </p><ul class="leading-tight" data-v-ef14abe7><li data-v-ef14abe7> Which option will produce the most good and do the least harm? </li><li data-v-ef14abe7> Which option best respects the rights of all who have a stake? </li><li data-v-ef14abe7> Which option best serves the community, not just some members? </li><li data-v-ef14abe7> Which option leads me to act as the person I want to be? (can be left for individual consideration), will help students to reach higher levels of moral reasoning than otherwise. Also, reminding the students to follow the RICE approach during the discussion is essential. Phase 4 could be assigned as homework as well. </li></ul>',11),H={__name:"Drugs-el",setup(n){const p=y();return(k,m)=>{const c=h,d=b,u=v,r=f,l=g,x=_;return w(),q("div",O,[o(d,null,{default:t(()=>[o(c,{class:"w-full lg:max-w-xs print:hidden",key:"climatechange-et"})]),_:1}),e("section",j,[o(u,{name:"openmoji:teacher-light-skin-tone",class:"float-right text-[6em] text-slate-300"}),z,e("section",D,[S,M,o(r),I]),E,N,$,e("section",L,[U,W,R,e("ul",null,[V,e("li",null,[a("Peer feedback (on the group presentation using the following "),o(l,{to:"../tools/peer-feedback",target:"_blank"},{default:t(()=>[a("tool")]),_:1}),a(" and self-assessment within the case study).")])]),B,F]),Q,e("section",J,[G,o(x,null,{default:t(()=>[a(" NB! Considering the topic's sensitivity (i.e. cannabis), teachers are expected to promote a safe, non-judgmental environment in this module, where all students can participate. Teachers should also have sound subject knowledge and be confident using suitable learning methodologies. (And be ready to reflect on their own assumptions about cannabis if students ask about it.) ")]),_:1}),K,e("p",null,[a(" All myths are backed up by "),o(l,{to:C(p)("/drugs/background")},{default:t(()=>[a("Scientific background information")]),_:1},8,["to"]),a(', which explains the state-of-the-art science behind them and enables the teacher to get a quick overview of the science covered by the "Drugs" module. ')]),Z])])])}}},Ie=A(H,[["__scopeId","data-v-ef14abe7"]]),X={class:"flex flex-col items-start lg:flex-row-reverse lg:justify-end xl:gap-10 2xl:gap-20"},Y={class:"e-article print:not-prose",lang:"pt",style:{hyphens:"auto"}},ee=i('<h1 class="mb-0">Drogas</h1><h5 class="font-normal uppercase leading-tight">Material para o professor</h5><section id="s-1"><h2 id="s-1t">Module description</h2><section class="e-card e-module__meta"><table class="m-0 leading-tight"><tr><td><b>Grupo Alvo:</b></td><td>Este material didático destina-se a jovens entre os 15 e os 18 anos de idade, tanto do ensino geral como do ensino profissional.</td></tr><tr><td><b>Duração prevista:</b></td><td>4-6 horas, a 45 min.</td></tr></table></section><p> Neste módulo, os alunos são convidados a investigar e desmistificar mitos comuns sobre drogas. Num vídeo introdutório é apresentada a história de um rapaz, depois do visionamento os alunos são convidados a refletir e a partilhar as suas primeiras ideias sobre o assunto. Depois, são-lhes apresentados vários mitos comuns relacionados com o tema e, em grupo, são desafiados a escolher um mito para desmistificar±. Tendo em conta a natureza deste tópico, esta atividade está planeada para ser realizada apenas com base em provas secundárias (pesquisas na Internet). Com esta atividade, os alunos aprendem a avaliar a fiabilidade das informações/fontes de informação (bases de dados de ciência / investigação, ciência popular e outros meios de comunicação) e a desenvolver as suas capacidades de investigação. Depois de tirarem conclusões, os alunos preparam-se para apresentar as suas descobertas aos colegas (ou a outras audiências) de uma forma relevante e convincente (por exemplo, através de vídeos, podcasts, cartazes ou slides). O módulo é concluído com um estudo de caso em que os alunos podem resolver um dilema utilizando os seus conhecimentos relacionados com o tema, incorporando-os em valores pessoais, sociais e morais. </p><p> O módulo é composto por material para o professor (sugestões pedagógicas e informação científica de base) e material para o aluno (um vídeo introdutório, fichas de trabalho interactivas, vídeos de factos e um tutorial em vídeo). Além disso, os alunos podem utilizar a &quot;ferramenta de fiabilidade&quot; para avaliar a fiabilidade da informação encontrada e os tutoriais para apresentar os seus resultados e conclusões. </p></section><hr class="mt-10 mb-40 print:my-10"><section id="s-2"><h2 id="s-2t">Objetivos de aprendizagem do módulo</h2><ul class="leading-tight"><li><b>Competências de cidadania:</b> desenvolver conhecimentos e atitudes dos alunos no sentido de tomarem decisões responsáveis sobre o consumo de canábis e/ou as &quot;drogas milagrosas&quot;. </li><li><b>Competências de uso de media:</b><ul class="my-0"><li>desenvolver as capacidades dos alunos para avaliar a fiabilidade da informação, apresentando as provas de forma relevante para um determinado público,</li><li>reagir de forma adequada e responsável à desinformação apresentada nas redes sociais.</li></ul></li><li><b>Competências digitais:</b> desenvolver as aptidões dos alunos na utilização de meios digitais enquanto apresentam os resultados da sua investigação aos outros grupos. </li><li><b>Competência científica:</b><ul class="my-0"><li>desenvolver a compreensão dos alunos sobre a ciência como uma forma de conhecimento, como uma prática social e porque devemos confiar na ciência;</li><li>desenvolver nos alunos atitudes de honestidade, objetividade, humildade intelectual e abertura de espírito;</li><li>desenvolver a sua capacidade de planear e conceber procedimentos para testar hipóteses e interpretar os resultados;</li><li>aprofundar os conhecimentos dos alunos sobre a canábis: mecanismo de ação, diferenças entre os aspectos químicos e biológicos dos diferentes tipos de canábis;</li><li>desenvolver uma compreensão das propriedades químicas e biológicas do MMS e dos graves riscos para a saúde decorrentes da sua utilização como droga.</li></ul></li><li><b>Competências sociais:</b> desenvolver nos alunos... <ul><li> uma melhor compreensão dos riscos pessoais e sociais associados ao consumo de canábis, incluindo os seus aspectos legislativos; </li><li> uma melhor compreensão da importância das escolhas pessoais e dos valores relacionados com a canábis na gestão do bem-estar pessoal. </li></ul></li></ul></section><hr class="mt-10 mb-40 print:my-10"><section id="s-3"><h2 id="s-3t">Conhecimentos prévios esperados</h2><ul class="leading-tight"><li>Conhecimento prévio sobre canábis.</li><li>Conhecimento prévio dos impactos psicológicos e sociais do consumo de estupefacientes.</li></ul></section><hr class="mt-10 mb-40 print:my-10">',8),ae={id:"s-4"},oe=e("h2",{id:"s-4t"},"Estrutura do módulo",-1),se=e("p",null,"Este módulo é composto por 6 atividades. A sequência das atividades é apresentada na Figura 1 e na Tabela 1 (ver abaixo).",-1),te=i('<p class="mt-20"><b>Tabela 1.</b> Atividades de aprendizagem do módulo de “drogas” </p><table><thead><tr><th class="w-1/5"><b>Atividade Nº</b></th><th>Descrição</th></tr></thead><tbody><tr><td><b>Atividade 1</b></td><td>A apresentação do vídeo introdutório.</td></tr><tr><td><b>Atividade 2</b></td><td> Discussão em grupos:<br> Escolher um mito para ser desmistificado. </td></tr><tr><td><b>Atividade 3</b></td><td> Em grupos: <ul><li>Colocar questões de hipótese e investigação pertinentes;</li><li>Procurar informações relevantes para confirmar ou desmistificar a hipótese / questão da investigação;</li><li>Avaliação da fiabilidade das evidências encontradas;</li><li>Tirar conclusões.</li></ul></td></tr><tr><td><b>Atividade 4</b></td><td>Transformar a informação / evidências num formato relevante para um determinado público (pares). Podem visualizar um tutorial para aprender a produzir um pequeno vídeo.</td></tr><tr><td><b>Atividade 5</b></td><td>Apresentações em grupo (juntamente com atividades de avaliação por pares).</td></tr><tr><td><b>Atividade 6</b></td><td>Os alunos, individualmente e em grupos, consideram um dilema moral relacionado com a canábis</td></tr></tbody></table>',2),ie=e("hr",{class:"mt-10 mb-40 print:my-10"},null,-1),ne=e("section",{id:"s-5"},[e("h2",{id:"s-5t"}," Requisitos para o ambiente físico "),e("p",null," Smartphone, computador, internet, equipamento de audiovisual para mostrar vídeos, papel A3 ou A2, marcadores de várias cores. ")],-1),re=e("hr",{class:"mt-10 mb-40 print:my-10"},null,-1),de={id:"s-6"},le=e("h2",{id:"s-6t"},"Avaliação",-1),ce=e("p",null,"Os alunos podem ser avaliados de diferentes formas ao longo do módulo, incluindo competências do processo científico (formular questões, pesquisar evidências, avaliar a credibilidade das fontes, retirar conclusões), competências gerais, tais como a capacidade de argumentação, e o conhecimento do conteúdo relacionado com o tema. Os tipos de avaliação incluem avaliações formativas baseadas na observação, fichas de trabalho individuais/grupo e avaliações sumativas baseadas em apresentações de grupo.",-1),ue=e("p",null,[e("b",null,"Avaliações que podem ser aplicadas neste módulo:")],-1),pe=e("p",null,[e("b",null,"Formativa:")],-1),me=e("li",null,"Feedback oral / escrito do professor (baseado em observações, questões colocadas, etc.) sobre o trabalho individual/grupo ao longo do módulo.",-1),he=e("p",null,[e("b",null,"Sumativa:")],-1),be=e("ul",null,[e("li",null,"Classificações atribuídas pelo professor à apresentação do grupo (com base na apresentação dos alunos e na sua capacidade de dar respostas/comentários relevantes)."),e("li",null,"Classificações atribuídas pelo professor às fichas de trabalho individuais ou de grupo.")],-1),ve=e("hr",{class:"mt-10 mb-40 print:my-10"},null,-1),fe={id:"s-7"},ge=e("h2",{id:"s-7t"},"Sugestões de ensino",-1),_e=e("p",null," Este módulo pode fazer parte de um programa escolar de prevenção de consumo de drogas e de comportamentos de risco. O professor deve estar familiarizado com esse programa e colaborar com outros profissionais (psicólogos escolares, animadores sociais, etc.), caso surjam situações pessoais relacionadas com o consumo durante o módulo e os alunos possam precisar de apoio. O professor deve lembrar-se que a confidencialidade deve ser respeitada, exceto quando houver suspeita ou provas de que alguém está em risco. ",-1),ye=e("p",null,[a(" O módulo começa com um "),e("a",{href:"https://www.youtube.com/watch?v=cN5KV_WJubw",target:"_blank",rel:"noopener noreferrer"},"vídeo introdutório"),a(" que abre o tema para suscitar mais questões e não respostas imediatas. Espera-se que depois de verem o vídeo (Atividade 1) os alunos, em grupos, se sintam intrinsecamente motivados para analisar mais aprofundadamente os mitos e crenças relacionados com as drogas. A intenção é envolvê-los em atividades relacionadas com uma melhor compreensão da questão - uma questão vista pelos alunos como relevante para as suas vidas e não apenas para o currículo. Também apela aos alunos para refletirem sobre os seus conhecimentos prévios e partilharem as suas concepções e pontos de vista com os colegas (Atividade 2). Na atividade 2, apresentam-se 9 mitos sobre a canábis e cada grupo deve escolher um para ser desmascarado. A atividade 2 deve ajudar os alunos a ser capaz de transformar o mito escolhido numa hipótese/questão de investigação que possa ser falsificada/ confirmada/ respondida (atividade 3). É possível que os alunos precisem de ajuda para a colocar. Por isso, o professor pode ajudá-los dando exemplos de boas (em relação às quais é possível testar ou responder com informação fidedigna) e más hipóteses/perguntas, antes de eles próprios as colocarem. "),e("a",{href:"https://www.thoughtco.com/testable-hypothesis-explanation-and-examples-609100#:~:text=Updated%20on%20January%2012%2C%202019,experiment%20using%20the%20scientific%20method.",target:"_blank",rel:"noopener noreferrer"},"Este recurso"),a(" pode ser útil para o efeito. ")],-1),we=e("p",null,[a(" Os "),e("a",{href:"https://www.youtube.com/playlist?list=PLuC4DJElPQrOmTPUWRrZh_W5VtuNTDsdI",target:"_blank",rel:"noopener noreferrer"},'"fact videos"'),a(" também podem ser visionados nesta etapa para recordar ou estudar factos relacionados com as drogas. Além disso, podem ser utilizados para este fim capítulos selecionados da informação científica de base. ")],-1),qe=e("p",null," Os mitos relacionados com as drogas não podem ser desmistificados (desmentidos) experimentalmente na sala de aula, espera-se que os alunos recolham provas de fontes secundárias para confirmar ou falsificar a sua hipótese/resposta à sua pergunta de investigação (Atividade 3). Ao comparar fontes de dados/meios de comunicação e o seu conteúdo, os alunos analisam criticamente a sua fiabilidade e tiram conclusões justificadas com base nas evidências. Como a avaliação da fiabilidade pode ser um verdadeiro desafio para os alunos, é-lhes fornecida uma ferramenta para procurar e analisar a informação. Além disso, o professor pode explicar a sua utilização, demonstrando e analisando fontes fiáveis e menos fiáveis, como exemplos de aprendizagem, antes de os alunos a implementarem de forma independente. ",-1),ke=e("b",null,"(Atividade 4)",-1),xe=e("b",null,"(Atividade 5)",-1),Ae=e("p",null," Na última atividade (Atividade 6), o desenvolvimento do módulo regressa ao vídeo introdutório e aos mitos apresentados. Com base no que foi aprendido nas fases anteriores, os alunos preparam-se para tomar decisões a nível individual ou de grupo no formato de um estudo de caso, onde têm a oportunidade de justapor e refletir sobre os conhecimentos científicos adquiridos, mas também sobre os seus valores pessoais e sociais. ",-1),Ce=i('<p> O vídeo de introdução e as atividades seguintes destinam-se a inspirar a reflexão sobre a tomada de decisões relacionadas com o consumo de canábis e as várias consequências que podem resultar de determinadas decisões. Incentivam o espetador a considerar diferentes opções durante o processo de tomada de decisão e a forma como as escolhas relacionadas com o consumo ou posse de canábis podem afetar ou ter impacto nas relações existentes. A literatura pedagógica reconhece que os jovens têm mais probabilidades de aprender quando são incentivados a refletir de forma apoiada. </p><p> Um <b>dilema moral</b> apresentado no final do módulo (Atividade 6) deve levar os alunos a refletir sobre as suas escolhas e as dos outros e sobre as consequências dessas escolhas para os diferentes intervenientes envolvidos numa determinada situação. As decisões potenciais e o raciocínio que lhes está subjacente baseiam-se principalmente na teoria do desenvolvimento moral de Kolberg, que inclui o seguinte (os exemplos para ilustrar os níveis são retirados da atividade 6, mas são igualmente possíveis outras interpretações): </p><h3> Nível 1 (Pré-Convencional) </h3><ol><li><b>Orientação para a obediência e a punição.</b> Os indivíduos concentram-se nas consequências directas das suas acções para si próprios. Uma ação é considerada moralmente errada porque o seu autor é punido. Comum nas crianças. As regras são fixas e absolutas. A questão principal é: &quot;como posso evitar o castigo?” <br><i>Por exemplo, Dani não deve ficar com a droga porque isso pode resultar num registo criminal para si, o que significa que é uma pessoa má.</i></li><li><b>Orientação para o interesse próprio (instrumental-relativista):</b> Pagar por um benefício. O comportamento correto é definido pelo que o indivíduo considera ser do seu interesse, ou pelo que é &quot;conveniente&quot;, mas entendido de uma forma restrita que não tem em conta a sua reputação ou as relações com grupos de pessoas. O raciocínio mostra um interesse limitado nas necessidades dos outros, mas apenas até ao ponto em que isso possa favorecer os interesses do próprio indivíduo. <br><i>Por exemplo, Dani deve aceitar ficar com a substância porque Marcos já fez vários favores a Dani no passado e assim agora pode retribuir.</i><br><i>Dani deve devolvê-la a Marcos porque um amigo criminoso será inútil.</i></li></ol><h3> Nível 2 (Convencional) </h3><ol start="3"><li><b>Acordo interpessoal e conformidade com as normas sociais.</b> Os indivíduos são receptivos à aprovação ou desaprovação dos outros, uma vez que esta reflecte os pontos de vista da sociedade. Tentam ser um &quot;bom rapaz&quot; ou uma &quot;boa rapariga&quot; para corresponder a essas expectativas, tendo aprendido que ser considerado bom beneficia o eu. O raciocínio da terceira fase pode julgar a moralidade de uma ação avaliando as suas consequências em termos das relações de uma pessoa, que agora começam a incluir coisas como o respeito, a gratidão e a &quot;regra de ouro&quot;. Capacidade de reconhecer boas ou más intenções. A atitude de bom rapaz/rapariga. <br><i>Por exemplo, Dani deve ficar com ela porque não pode desiludir o seu amigo de longa data.</i><br><i>Por exemplo, Dani deve devolvê-la para não desiludir os seus pais, caso seja apanhado com drogas pela polícia.</i></li><li><b>Orientação para a manutenção da autoridade e da ordem social:</b> Moral da lei e da ordem, capacidade de ver sistemas normativos abstractos (&quot;Cumpre o teu dever&quot;). É importante obedecer às leis e às convenções sociais devido à sua importância para a manutenção de uma sociedade funcional. O raciocínio moral na quarta fase ultrapassa, assim, a necessidade de aprovação individual que se manifesta na terceira fase. Um ideal ou ideais centrais prescrevem frequentemente o que é certo e errado. Se uma pessoa violar uma lei, talvez todos o façam - assim, há uma obrigação e um dever de respeitar as leis e as regras. Quando alguém viola uma lei, isso é moralmente errado. <br><i>Por exemplo, Dani deve devolvê-la a Marcos porque ter e consumir erva é proibido.</i></li></ol><h3> Nível 3 (Pós-Convencional) </h3><ol start="5"><li><b>Orientação para o contrato social</b> - as diferentes opiniões, direitos e valores devem ser mutuamente respeitados como sendo exclusivos de cada pessoa ou comunidade. As leis são encaradas como <a href="https://en.wikipedia.org/wiki/Social_contract" target="_blank" rel="noopener noreferrer">contratos sociais</a> e não como decretos rígidos. Os direitos pessoais são importantes. Isto é conseguido através de uma decisão maioritária e de um compromisso inevitável. O governo democrático é supostamente baseado no raciocínio da quinta fase. <br><i>Por exemplo, Dani deve ficar com ela para evitar que o seu amigo seja condenado a uma pena criminal, o que o pode levar ainda mais longe na sua queda pessoal.</i><br><i>Por exemplo, Dani deve devolvê-la, pois tentar evitar que o Marcos seja condenado por uma pequena quantidade de estupefacientes pode levá-lo a um caso mais significativo relacionado com estupefacientes. Poderia assim receber uma lição de vida para evitar o tráfico de drogas.</i><br><i>Por exemplo, Dani não deve ficar com ela, pois isso pode resultar num registo criminal que impeça a entrada na academia militar.</i><br><i>Por exemplo, Dani deve devolvê-la, pois não seria justo ter um castigo por um ato que não cometeu.</i></li><li><b>Princípios éticos universais</b> - o raciocínio moral baseia-se num <a href="https://en.wikipedia.org/wiki/Abstraction" target="_blank" rel="noopener noreferrer">raciocínio abstrato</a> que utiliza princípios éticos universais. O consenso resultante é a ação tomada. O indivíduo age porque é correto e não porque evita a punição, é do seu interesse, é esperado, é legal ou foi previamente acordado. <br><i>Por exemplo, Dani devia devolver a substância e deixar Marcos assumir toda a responsabilidade pelo que fez. Os estupefacientes arruinaram a vida de muitas pessoas. Não é correto apoiar o seu uso e propagação.</i></li></ol><hr><p> Através da atividade 6, espera-se que os alunos desenvolvam uma compreensão mais ampla da questão, considerando diferentes perspectivas e consequências de uma ou outra decisão. Além disso, espera-se como resultado da fase 4 que a resposta às seguintes perguntas: </p><ul class="leading-tight"><li> Que opção produzirá o maior bem e causará o menor dano? </li><li> Qual a opção que melhor respeita os direitos de todos os interessados? </li><li> Qual é a opção que melhor serve a comunidade e não apenas alguns membros? </li><li> Que opção me leva a agir como a pessoa que quero ser? (pode ser deixado à consideração de cada um) </li></ul><p> ajude os alunos a atingir níveis mais elevados de raciocínio moral do que de outra forma. Além disso, é essencial lembrar aos alunos que devem seguir a abordagem RICE durante o debate. A fase 4 também pode ser atribuída como trabalho de casa. </p>',12),Ee={__name:"Drugs-pt",setup(n){return y(),(p,k)=>{const m=h,c=b,d=v,u=f,r=g,l=_;return w(),q("div",X,[o(c,null,{default:t(()=>[o(m,{class:"w-full lg:max-w-xs print:hidden",key:"climatechange-et"})]),_:1}),e("section",Y,[o(d,{name:"openmoji:teacher-light-skin-tone",class:"float-right text-[6em] text-slate-300"}),ee,e("section",ae,[oe,se,o(u),te]),ie,ne,re,e("section",de,[le,ce,ue,pe,e("ul",null,[me,e("li",null,[a("Feedback dos pares (sobre a apresentação do grupo utilizando esta "),o(r,{to:"../tools/peer-feedback",target:"_blank"},{default:t(()=>[a("ferramenta")]),_:1}),a(" e a autoavaliação no âmbito do estudo de caso).")])]),he,be]),ve,e("section",fe,[ge,o(l,null,{default:t(()=>[a(" Tendo em conta a sensibilidade do tema (ou seja, a canábis), espera-se que os professores promovam neste módulo um ambiente seguro e sem preconceitos, onde todos os alunos possam participar. Os professores devem também ter bons conhecimentos sobre o assunto e estar confiantes na utilização de metodologias de aprendizagem adequadas. (E estar preparados para refletir sobre as suas próprias suposições sobre o canábis se os alunos perguntarem sobre o assunto). ")]),_:1}),_e,ye,we,qe,e("p",null,[a(" Para comunicar os seus resultados, os alunos são desafiados a produzir um vídeo ou outro tipo de suporte visual, audio ou audiovisual, para apresentarem as suas conclusões "),ke,a(". Para este efeito podem utilizar diferentes tutoriais. Além disso, o material do aluno é fornecido com critérios que a apresentação deve cumprir. Estes critérios podem ser utilizados de forma formativa para autoavaliação pelos alunos durante o processo e para avaliação pelos pares quando apresentam o vídeo e respondem às suas perguntas e às do professor "),xe,a(". Para esta última, os alunos podem utilizar a seguinte "),o(r,{to:"../tools/peer-feedback",target:"_blank",class:"btn btn-sm btn-accent text-white no-underline"},{default:t(()=>[o(d,{name:"mdi:tools",size:"20"}),a(" ferramenta")]),_:1}),a(" de avaliação para dar feedback aos outros grupos. ")]),Ae,e("p",null,[a(" Para todos os mitos existe "),o(r,{to:"./drugs/background"},{default:t(()=>[a("informação científica de base")]),_:1}),a(" sobre o tema, que explica os conhecimentos atuais da ciência que lhes está subjacente e permite ao professor ter uma visão rápida da ciência abrangida pelo módulo. ")]),Ce])])])}}};export{Ie as _,Ee as a};
